import i18n from 'i18next'
import Backend from 'i18next-xhr-backend'
import { initReactI18next } from 'react-i18next'

i18n
    .use(Backend)
    .use(initReactI18next)
    .init ({
        lng: 'en',
        resources: {
            en: {
                translation: {
                    "taxyear.label": "Tax Year"
                }
            },
            zh: {
                translation: {
                    "taxyear.label": "課稅年度"
                }
            }
        },
        fallbackLng: 'zh-hant',
        debug: true,
        keySeparator: false,
        interpolation: {
            escapeValue: false,
            formatSeparator: ','
        },
        react: {
            wait: true
        }
    })

export default i18n
