import React from 'react'
import i18n from './i18n';
import { useTranslation } from 'react-i18next'
import { withNamespaces } from 'react-i18next';

const LanguageSelector = () => {

    const { t, i18n } = useTranslation()

    const changeLanguage = (lng) => {
        i18n.changeLanguage(lng)
    }

    return (
        <div onChange={changeLanguage}>
            <input type='radio' value='en' name='language' onClick={() => changeLanguage('en')} defaultChecked/>English
            <input type='radio' value='zh-hant' name='language' onClick={() => changeLanguage('zh-hant')}/>中文-繁體
        </div>
    )

}

export default withNamespaces()(LanguageSelector);
